-- A solution contains projects, and defines the available configurationsdofile "protobuf.lua"solution "muduo"    location "build"    language "C++"    configurations { "Debug", "Release" }    objdir "build/obj"    includedirs {        "."    }    flags {        "Symbols",        "ExtraWarnings",        --"FatalWarnings",    }    buildoptions {        "-Wconversion",        "-Wno-unused-parameter",        "-Wold-style-cast",        "-Woverloaded-virtual",        "-Wpointer-arith",        "-Wshadow",        "-Wwrite-strings",    }    libdirs {        "bin/Debug"    }    configuration "Debug"        targetdir "build/Debug"        defines { "_DEBUG" }    configuration "Release"        targetdir "build/Release"        defines { "NDEBUG" }        flags { "Optimize" }        buildoptions {            "-finline-limit=1000",        }    configuration "macosx"        libdirs {            "/opt/local/lib",        }     -- A project defines one build target    project "muduo"        kind "StaticLib"        files { "muduo/**.h", "muduo/**.cc" }        excludes {            "**/tests/*",            --"muduo/net/protorpc/*",        }        protobufs "muduo/**/*.proto"        configuration "macosx"            excludes { "**/EPollPoller.cc" }local unittests = os.matchfiles("muduo/**/tests/*.cc")for _,unittest in ipairs(unittests) do    project(path.getbasename(unittest))        kind "ConsoleApp"        files(unittest)        links {            "muduo",            "boost_unit_test_framework-mt",        }end