-- A solution contains projects, and defines the available configurationssolution "muduo"    location "build"    configurations { "Debug", "Release" }    objdir "obj"     -- A project defines one build target    project "muduo"        kind "StaticLib"        language "C++"        files { "muduo/**.h", "muduo/**.cc" }        excludes {            "**/tests/*",            "muduo/net/protorpc/*",        }        flags {            "Symbols",            "ExtraWarnings",            --"FatalWarnings",        }        buildoptions {            "-Wconversion",            "-Wno-unused-parameter",            "-Wold-style-cast",            "-Woverloaded-virtual",            "-Wpointer-arith",            "-Wshadow",            "-Wwrite-strings",        }        includedirs {            "."        }        libdirs {        }        configuration "Debug"            --defines { "DEBUG" }            targetdir "bin/Debug"        configuration "Release"            defines { "NDEBUG" }            flags { "Optimize" }            buildoptions {                "-finline-limit=1000",            }            targetdir "bin/Release"        configuration "macosx"            excludes {                "**/EPollPoller.cc"            }